mapper.select:

select count(*) from booking_3
where storecode = #{storecode}
  and id = #{id}
  and bookingdate = #{bookingdate}
  and abs(
        #{bookingmin}
        -
        (to_number(substr(bookingtime,1,2))*60 + to_number(substr(bookingtime,4,2)))
      ) < 120


